package module2.lesson13;

import java.util.concurrent.*;

class Suma implements Runnable {
    private final Dato dato;

    public Suma(Dato dato) {
        this.dato = dato;
    }

    @Override
    public void run(){
        synchronized (dato) {
            System.out.println(Thread.currentThread().getName() + "-suma - entrar");
            int valor = dato.getDato();
            dato.setDato(valor + 100);
            System.out.println(Thread.currentThread().getName() + "-suma - salir");
        }
    }
}

class Resta implements Runnable {

    private final Dato dato;

    public Resta(Dato dato) {
        this.dato = dato;
    }

    @Override
    public void run(){
        synchronized (dato) {
            System.out.println(Thread.currentThread().getName() + "-resta - entrar");
            int valor = dato.getDato();
            dato.setDato(valor - 100);
            System.out.println(Thread.currentThread().getName() + "-resta - salir");
        }
    }
}

class Dato{
    private int dato;

    public int getDato() {

        try {
            TimeUnit.SECONDS.sleep(1);
        } catch (InterruptedException e) {
            throw new RuntimeException(e);
        }

        return dato;
    }

    public void setDato(int dato) {

        try {
            TimeUnit.MILLISECONDS.sleep(300);
        } catch (InterruptedException e) {
            throw new RuntimeException(e);
        }

        this.dato = dato;
    }

}

